function loadInfoWindow(marker, context) {
  var map = $('.leodismap').gmap3("get"),
  infowindow = $('.leodismap').gmap3({get:{name:"infowindow"}});
  if (infowindow){
    infowindow.open(map, marker);
    infowindow.setContent(context.data);
  } else {
    $('.leodismap').gmap3({
      infowindow:{
        anchor:marker,
        options:{content: context.data, disableAutoPan:true}
      }
    });
  }
  map.panTo(marker.position);
}




